import React from "react";
import Block from "./block";
import GridLayout from "react-grid-layout";

import "react-grid-layout/css/styles.css";
import "react-resizable/css/styles.css";

export default class BigDisplay extends React.Component {
  state = { allColour: -1 };

  constructor(props) {
    super(props);
    this.state.allColour = props.colour;
  }
  render() {
    var layout = { i: "a", x: 10, y: 100, h: 20, minW: 10, maxW: 10 };

    return (
      <GridLayout
        className="layout"
        layout={layout}
        cols={12}
        rowHeight={30}
        width={1200}
      >
        <div key="a">
          <div>
            <Block colour={this.state.allColour} />
            <Block colour={this.state.allColour} />
            <Block colour={this.state.allColour} />
          </div>
          <div>
            <Block colour={this.state.allColour} />
            <Block colour={this.state.allColour} />
            <Block colour={this.state.allColour} />
          </div>
          <div>
            <Block colour={this.state.allColour} />
            <Block colour={this.state.allColour} />
            <Block colour={this.state.allColour} />
          </div>
        </div>
      </GridLayout>
    );
  }
}
