import React from "react";
import ReactDOM from "react-dom";
import Block from "./block";
import "./App.css";
import { Button } from "reactstrap";

export default class BigDisplay extends React.Component {
  state = { allColour: "2" };

  constructor(props) {
    super(props);
    this.state.allColour = props.colour;
  }
  render() {
    return (
      <div>
        <div>
          <Block colour={this.state.allColour} />
          <Block colour={this.state.allColour} />
          <Block colour={this.state.allColour} />
        </div>
        <div>
          <Block colour={this.state.allColour} />
          <Block colour={this.state.allColour} />
          <Block colour={this.state.allColour} />
        </div>
        <div>
          <Block colour={this.state.allColour} />
          <Block colour={this.state.allColour} />
          <Block colour={this.state.allColour} />
        </div>
      </div>
    );
  }
}
ReactDOM.render(<BigDisplay />, document.getElementById("root"));
